import React, { Component } from 'react'
import styles from './timer.css'

export default class Timer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            minutes: props.initialTimer, 
            seconds: 0, 
            sectionType: props.sectionType,
            isTestInReview: props.isTestInReview
        }
    }


    componentDidMount() {
       
        this.myInterval = setInterval(() => {
            const { seconds, minutes } = this.state

            if (seconds > 0) {
                this.setState(({ seconds }) => ({
                    seconds: seconds - 1
                }))
            }
            if (seconds === 0) {
                if (minutes === 0) {
                    this.props.onTimeEnd();
                } else {
                    this.setState(({ minutes }) => ({
                        minutes: minutes - 1,
                        seconds: 59
                    }))
                }
            } 
        }, 1000)
    }

    static getDerivedStateFromProps(nextProps, prevProps) {
        if(nextProps.sectionType !== prevProps.sectionType){
            return {
                minutes: nextProps.initialTimer, 
                seconds: 0,
                sectionType: nextProps.sectionType,
            };
        }

    }

    componentWillUnmount() {
        clearInterval(this.myInterval)
    }

    renderTimerText() {
        const { minutes, seconds } = this.state;
        if (minutes === 0 && seconds === 0) {
            return (
                <h1>Time's up!</h1>
            );
        } else if (minutes === 0 & seconds <= 15) {
            return (
                <h1>### WARNING ### Time Remaining: {minutes < 10 ? `0${minutes}`: minutes} :{seconds < 10 ? `0${seconds}` : seconds}</h1>
            )
        } else {
            return (
                <h1>
                    {minutes < 10 ? `0${minutes}`: minutes}:{seconds < 10 ? `0${seconds}` : seconds}
                </h1>
            );
        }

    }
    displayTimer() {
        return (
            <div className="timerContainer">
            <div className="sectionType">
            <h2>{this.props.sectionType}</h2>
            </div>
            <div className="eliteGeneral">
            <a href="https://www.facebook.com/eliteprepvn/" className="elite">ELITE PREP VIETNAM</a>
            </div>
            <div className="timer">
            {this.renderTimerText()}
            </div>
        </div>
        )
    }
    notDisplayTimer() {

    }
    render() {
        const {isTestInReview} = this.state;
        return (
            <div className="timerContainer">
                {
                }
                <div className="sectionType">
                <h2>{this.props.sectionType}</h2>
                </div>
                <div className="eliteGeneral">
                <a href="https://www.facebook.com/eliteprepvn/" className="elite">ELITE PREP VIETNAM</a>
                </div>
                <div className="timer">
                {this.renderTimerText()}
                </div>
            </div>
        )
    };
}
