import React from "react";
import {Button } from "reactstrap"; 
import './button.css';


const ButtonComponent = props => {
  return (
    <div>
      <p><b>  {props.question} </b></p>

      <div className="choice">
        <div className='mb-2' >
        { 
            props.choices.map((choice) => {
              const selected = props.selectedChoice === choice.id;
              const correctlyAnswer = choice.find((selectedChoice)=> selectedChoice.check === true);
              const selectedCorrectly =  props.selectedChoice  === choice.correctlyAnswer;
              return(
                <div key={choice.id} className="mt-2">
                  <Button
                    key={choice.id}
                    onClick={() => !props.isTestSubmitted && props.onClick(choice.id)}  
                    className="btn-circle" 
                    outline={!selected} 
                    color={props.isTestSubmitted ? "success" : "warning"}>
                    {choice.opt}
                </Button>
                <span className="m-2">
                  {choice.text}
                </span>
              </div>
              )
            })
        }
        </div>
      </div>
    </div>

  );
}

export default ButtonComponent;
