import React from "react";
import { Button } from "reactstrap";
import "./button.css";

const ButtonComponent = props => {
  return (
    <div>
      <p><b> {props.question} </b></p>
      <div className="choice">
        <div className='mb-2' >
          {
            props.questions.map ( choice => {
              return (
                <Button
                onClick={() => props.onClick(props.questions.choice.id)}  
                className="btn-circle" 
                outline={props.selectedChoice !== props.questions.choice.id} 
                color="primary">
                  A
              </Button>
              )
            })
          }
          {props.questions.choice.text}
        </div>
      </div>
    </div>

  );
}

export default ButtonComponent;
